import React, { useState, useEffect, useCallback, Component } from 'react';
import { View, Text, StyleSheet, Switch, Platform} from 'react-native';
import { HeaderButtons, Item } from 'react-navigation-header-buttons';
import { Slider } from 'react-native-elements';
import DatePicker from 'react-native-datepicker'

import HeaderButton from '../components/HeaderButton';
import Colors from '../constants/Colors';


class FiltersScreen extends Component {

    state = {
        isLoading: true,
        minDate : '',
        maxDate: '',
        maxKm: 0,
        minKm: 0,
        minKg: 0, 
        maxKg: 0, 
        transports: [],
        foods: [],
        currentKg:0,
        currentKm:0,
        maxCurrentDate: '',
        minCurrentDate: ''
    }

    componentDidMount = () => {
            fetch('http://192.168.1.4:5000/getUserDetails', {
                    headers: {
                            'Accept': 'application/json',
                            'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                            userId:1
                    }),
                    method: 'POST'
            })
            .then((response) => response.json())
            .then((responseJson) => {
                            console.log(responseJson);
                            this.setState({
                                    userId: responseJson['userDetails']['id'],
                                    email: responseJson['userDetails']['email'],
                                    username: responseJson['userDetails']['username'],
                                    userEnergy: responseJson['userDetails']['energyTotal'],
                                    password: responseJson['userDetails']['password'],
                                    isLoading: !this.state.isLoading })
                            console.log(this.state);

            })
            .catch((error) => {
                     console.error(error);
                     alert("User data didnt fetch");
                    });
            }
    // const { navigation } = props;

    // const [isGlutenFree, setIsGlutenFree] = useState(false);
    // const [isLactoseFree, setIsLactoseFree] = useState(false);
    // const [isVegan, setIsVegan] = useState(false);
    // const [isVegetarian, setIsVegetarian] = useState(false);
    // const [sliderCostValue, setSliderCostValue] = useState(0);
    // const [sliderHoursValue, setSliderHoursValue] = useState(0);


    // const [startDate, setStartDate] = useState(0);
    // const [endDate, setEndDate] = useState(0);

    // const saveFilters = useCallback(() => {
    //     const appliedFilters = {
    //         glutenFree: isGlutenFree,
    //         lactoseFree: isLactoseFree,
    //         vegan: isVegan,
    //         isVegetarian: isVegetarian
    //     };

    //     console.log(appliedFilters);
    // }, [isGlutenFree, isLactoseFree, isVegan, isVegetarian]);

    // useEffect(() => {
    //     navigation.setParams({ save: saveFilters });
    // }, [saveFilters]);



    updateMinCurrentDate = (minCurrentDate) => {
        this.setState({ minCurrentDate: minCurrentDate })
    }
    updateMaxCurrentDate = (maxCurrentDate) => {
        this.setState({ maxCurrentDate: maxCurrentDate })
    }

    updateCurrentKm = (minKm) => {
        this.setState({ minKm: minKm })
    }
    updateCurrentKg = (maxKg) => {
        this.setState({ maxKg: maxKg })
    }

    render() {
        return (
            <View style={styles.screen}>
                <View style={styles.component}>
                    <DatePicker
                        style={{width: 300}}
                        date={this.state.minCurrentDate}
                        mode="date"
                        placeholder="select date"
                        format="YYYY-MM-DD"
                        minDate={this.state.minDate}
                        maxDate={this.state.maxDate}
                        confirmBtnText="Confirm"
                        cancelBtnText="Cancel"
                        onDateChange={this.updateMinCurrentDate}
                    />
                </View>
            <View style={styles.component}>

                <DatePicker
                    style={{width: 300}}
                    date={this.state.maxCurrentDate}//{this.state.date}
                    mode="date"
                    placeholder="select date"
                    format="YYYY-MM-DD"
                    minDate={this.state.minDate}
                    maxDate={this.state.maxDate}
                    confirmBtnText="Confirm"
                    cancelBtnText="Cancel"
                    onDateChange={this.updateMaxCurrentDate}
                    />    
                </View>

                <View style={styles.component}>
                    <Text style={styles.text}>Food Kg: {this.state.currentKg}</Text>

                    <View style={styles.slider}>

                        <Slider
                            value={this.currentKg}
                            onValueChange={this.updateCurrentKg}
                            maximumValue={this.state.maxKg}
                            minimumValue={this.state.minKg}
                            step={5}
                            trackStyle={{ height: 10, backgroundColor: 'transparent' }}
                            thumbStyle={{ height: 20, width: 20, backgroundColor: Colors.primaryColor }}
                        />

                    </View>
                </View>

            <View style={styles.component}>
                <Text style={styles.text}>Transport Km: {this.state.currentKm}</Text>
                <View style={styles.slider}>

                    <Slider
                        value={this.currentKm}
                        onValueChange={this.updateCurrentKm}
                        maximumValue={this.state.maxKm}
                        minimumValue={this.state.minKm}
                        step={5}
                        trackStyle={{ height: 10, backgroundColor: 'transparent' }}
                        thumbStyle={{ height: 20, width: 20, backgroundColor: Colors.primaryColor }}
                    />
                </View>
                </View>
            </View>
        )

    }
};














FiltersScreen.navigationOptions = navData => {
    return {
        headerTitle: 'Filters',
        headerLeft: (
            <HeaderButtons HeaderButtonComponent={HeaderButton}>
                <Item
                    title="Menu"
                    iconName="ios-menu"
                    onPress={() => {
                        navData.navigation.toggleDrawer();
                    }}
                />
            </HeaderButtons>
        ),
        headerRight:(
            <HeaderButtons HeaderButtonComponent={HeaderButton}>
                <Item
                    title="Save"
                    iconName="ios-save"
                    onPress={navData.navigation.getParam('save')}
                />
            </HeaderButtons>
        )
    };
};

const styles = StyleSheet.create({
    screen: {

        flex: 1,
        alignItems: 'center',
        marginBottom: 15,
        position: 'relative',
        justifyContent: 'center',
        padding: 5,
        backgroundColor: '#ffffff',
    },

    filterContainer: {
        flexDirection: 'row',
        alignItems: 'stretch',
        marginVertical: 15
    },
    sameRow:{
        flex:1,
        flexDirection:'row'
    },
    text: {
        height: 30
    },
    component: {
        borderRadius: 8,
        paddingLeft: 25,
        alignSelf: 'stretch',
        // justifyContent: 'center'
    },
    slider:{
        width:"75%"
    }
});

export default FiltersScreen;
