import React, { Component } from 'react'
import { HeaderButtons, Item } from 'react-navigation-header-buttons';
import { View, Text, TextInput, StyleSheet, TouchableOpacity } from 'react-native';

import HeaderButton from '../components/HeaderButton';
import { Ionicons } from '@expo/vector-icons'; 

import Colors from '../constants/Colors';

class SettingsScreen extends Component {

  state = {
    username : '',
    email: '',
    energyTotal: 0,
    isLoading: true
  }

  componentDidMount = () => {
    fetch('http://192.168.1.4:5000/getUserDetails', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        userId:1
      }),
      method: 'POST'
    })
    .then((response) => response.json())
    .then((responseJson) => {
        console.log(responseJson);
        this.setState({
          email: responseJson['email'],
          username: responseJson['username'],
          energyTotal: responseJson['energyTotal'],
          isLoading: !this.state.isLoading })

    })
    .catch((error) => {
       console.error(error);
       alert("User data didnt fetch");
      });
    }


    updateSettings = (username, email, energyTotal) => {
      alert('username: ' + username + ' email: ' + email +' energyTotal: ' + energyTotal)
    }

    render() {
      console.log(this.state);

      // if(!this.state.loading) {
        return (
        <View  style = {styles.screen}>


        <View style={styles.inputLabels}>
        <Text> </Text>

        <TouchableOpacity  onPress={()=>this.openTransportModal()}  underlayColor="white">
          <View style={styles.button}>
            <Ionicons name="ios-add-circle-outline" size={24} color="white" />
            <Text style={styles.buttonText}> Transport Emission</Text>
          </View>
        </TouchableOpacity>

        <Text> </Text>
        <TouchableOpacity  onPress={()=>this.openFoodModal()}  underlayColor="white">
          <View style={styles.button}>
            <Ionicons name="ios-add-circle-outline" size={24} color="white" />
            <Text style={styles.buttonText}> Food Emission</Text>
          </View>
        </TouchableOpacity>
        </View>
















          <Text style = {styles.text}>Update Username </Text>
          <TextInput style = {styles.input}
            underlineColorAndroid = "transparent"
            placeholder = {this.state.username}
            // placeholderTextColor = {Colors.primaryColor}
            autoCapitalize = "none"
            onChangeText = {this.handleHours}/> 

           <Text style = {styles.text}>Update Email </Text>

          <TextInput style = {styles.input}
            underlineColorAndroid = "transparent"
            placeholder = {this.state.email}
            // placeholderTextColor = {Colors.primaryColor}
            autoCapitalize = "none"
            onChangeText = {this.handleHours}/>

           <Text style = {styles.text}>Update Total Energy </Text>

          <TextInput style = {styles.input}
            underlineColorAndroid = "transparent"
            keyboardType = 'numeric'
            placeholder = ''//{(this.state.energyTotal).toString()}

            // placeholderTextColor = {Colors.primaryColor}
            autoCapitalize = "none"
            onChangeText = {this.handleenergyTotal}/>

          <Text > </Text>

          <TouchableOpacity  onPress={()=>this.updateSettings(this.state.username, this.state.email, this.state.energyTotal)}  underlayColor="white">
          <View style={styles.button}>
            <Text style={styles.buttonText}> Save</Text>
          </View>
        </TouchableOpacity> 

        </View>
        )
      // }
      // else{
      //   return (
      //     <ActivityIndicator size={"large"} ></ActivityIndicator>)
      // }
    }

  // return <MealList listData={favMeals} navigation={props.navigation} />;
};

SettingsScreen.navigationOptions = navData => {
  return {
    headerTitle: 'Settings',
    headerLeft: (
      <HeaderButtons HeaderButtonComponent={HeaderButton}>
        <Item
          title="Menu"
          iconName="ios-menu"
          onPress={() => {
            navData.navigation.toggleDrawer();
          }}
        />
      </HeaderButtons>
    )
  };
};

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 15,
    position: 'relative',
    backgroundColor: '#ffffff'
  },
  inputLabels:{
    justifyContent: 'flex-start',
    padding: 10
  },
  title: {
    fontFamily: 'open-sans-bold',
    fontSize: 12,
    margin: 20,
    textAlign: 'center'
  },
  input:{
    width: "75%",
    zIndex: 2,
    borderBottomWidth:1
  },

  button: {

    display: 'flex',
    flexDirection: 'row',
    height: 35,
    borderRadius: 6,
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    backgroundColor: '#2AC062',
    shadowColor: '#2AC062',
    shadowOpacity: 0.5,
    shadowOffset: { 
      height: 10, 
      width: 0 
    },
    shadowRadius: 25,
    backgroundColor:  Colors.primaryColor
    },
    buttonText: {
      textAlign: 'center',
      padding: 20,
      color: 'white'
    },
  text:{
      height: 36,
      borderRadius: 8,
      marginLeft: 20,
      marginTop: 20,
      alignSelf: 'stretch',
      // justifyContent: 'center'
  },
  container: {
    paddingTop: 15
  },
  button: {

    display: 'flex',
    flexDirection: 'row',
    height: 60,
    borderRadius: 6,
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    backgroundColor: '#2AC062',
    shadowColor: '#2AC062',
    shadowOpacity: 0.5,
    shadowOffset: { 
      height: 10, 
      width: 0 
    },
    shadowRadius: 25,
    backgroundColor:  Colors.primaryColor
    },
    buttonText: {
      textAlign: 'center',
      padding: 20,
      color: 'white'
    }
});

export default SettingsScreen;
