import React, { Component } from 'react'
import {
  View,
  Text,
  TextInput,
  ActivityIndicator,
  StyleSheet,
  Picker,
  Button,
  TouchableOpacity
} from 'react-native';
import { HeaderButtons, Item } from 'react-navigation-header-buttons';
import Modal from 'react-native-modal';

import HeaderButton from '../components/HeaderButton';
import Colors from '../constants/Colors';
import {
  PieChart,
  ProgressChart
} from 'react-native-chart-kit'

class CategoriesScreen extends Component {

  state = {
    email: '',
    password: '',
    user: '',
    energyType:'',
    transport: '',
    food: '',
    hours: '',
    transportData: [],
    foodData: [],
    isLoading: true,
    isDisabled: true,
    isTransportModalVisible: false,
    isFoodModalVisible: false

 }


  componentDidMount = () => {
    fetch('http://0.0.0.0:5000/getTransportObjects', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      method: 'GET'
    })
    .then((response) => response.json())
    .then((responseJson) => {
      console.log(responseJson);
      for (const key in responseJson['transportObjects']){
        // console.log("key:",responseJson['transportObjects'][key])
        this.state.transportData.push(responseJson['transportObjects'][key])
        }
      for (const key in responseJson['foodObjects']){
        // console.log("key:",responseJson['transportObjects'][key])
        this.state.foodData.push(responseJson['foodObjects'][key])
        }
        this.setState({isLoading: !this.state.isLoading })
    })
    .catch((error) => {
      console.error(error);
      alert("Transport data didnt fetch");

    });
  }



  insertOrder = (energyType, transport, hours) => {
    fetch('http://0.0.0.0:5000/insertOrder', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      method: 'POST',
      body: JSON.stringify({
        // energyType: energyType,
        item: transport,
        time: hours
     })
    })
    .then((response) => response.json())
    .then((responseJson) => {
      console.log(responseJson);
    })
    .catch((error) => {
      console.error(error);
      alert("Insert Order Error");

    });
  }

  updateTransport = (transport) => {
    this.setState({ transport: transport })
  }

  updateFood = (food) => {
    this.setState({ food: food })
  }

  handleEmail = (text) => {
    this.setState({ email: text })
  }
  handlePassword = (text) => {
    this.setState({ password: text })
  }
  handleHours = (float) => {
    this.setState({ hours: float })
  }
  handleTransport = (text) => {
  this.setState({ transport: text })
  }
  login = (transport, hours) => {
    alert('Transport: ' + transport + ' Hours: ' + hours)
  }




  openTransportModal = () =>{
    this.setState({
      isTransportModalVisible:true
    })
    }

  toggleTransportModal = () =>{
    this.setState({
      isTransportModalVisible:!this.state.isTransportModalVisible
    })
    }

  closeTransportModal = () =>{
    this.setState({
      isTransportModalVisible:false
    })
    }

  openFoodModal = () =>{
    this.setState({
      isFoodModalVisible:true
    })
    }

  toggleFoodModal = () =>{
    this.setState({
      isFoodModalVisible:!this.state.isFoodModalVisible
    })
    }

  closeFoodModal = () =>{
    this.setState({
      isFoodModalVisible:false
    })
    }






  render() {

    console.log(this.state.transportData);
    console.log(this.state.foodData);

    // const data = [0.4, 0.6, 0.8]

    const data = {
      labels: ['Transport', 'Food', 'Total'],
      data: [ 0.2, 0.45, 1 ]
    }
    const chartConfig = {
      backgroundGradientFrom: '#1E2923',
      backgroundGradientTo: '#08130D',
      color: (opacity = 1) => `rgba(26, 255, 146, ${opacity})`
    }


    if(!this.state.loading) {
      return (
        <View style = {styles.container}>
        <ProgressChart
          data={data}
          width={400}
          height={220}
          chartConfig={chartConfig}
        />




        <TouchableOpacity  onPress={()=>this.openTransportModal()}  underlayColor="white">
          <View style={styles.button}>
            <Text style={styles.buttonText}> Choose Food</Text>
          </View>
        </TouchableOpacity>

        <View>

          <Modal animationIn="slideInUp" 
              animationOut="slideOutDown" 
              onBackdropPress={()=>this.closeTransportModal()}
              onSwipeComplete={()=>this.closeTransportModal()} 
              swipeDirection="right" 
              isVisible={this.state.isTransportModalVisible} 
              width="100%"
              style={{backgroundColor:'white'}}>

          <View style={{ flex: 1,justifyContent:'center'}}>
            <Picker selectedValue = {this.state.transport} 
              onValueChange = {this.updateTransport} 
              style = {styles.input}>
                {this.state.transportData.map((transports, id) => {
                  return <Picker.Item 
                    value={transports.item} 
                    label={transports.description}
                    key={transports.id}  /> 

                    }
                    )}
            </Picker>   

            <Text style = {styles.text}>Add Hours: </Text>

            <TextInput style = {styles.input}
              underlineColorAndroid = "transparent"
              placeholder = " Hours (fe 1.3)"
              placeholderTextColor = {Colors.primaryColor}
              autoCapitalize = "none"
              onChangeText = {this.handleHours}/>     
          </View>


          <View style={{ flex: 1,justifyContent:'center',position:'absolute',bottom:0}}>
              <View style={{flexDirection:'row',}}>
              <TouchableOpacity style={{backgroundColor:'red',width:'50%'}} onPress={()=>this.insertOrder()}>
                <Text style={{color:'white',textAlign:'center',padding:10}}>Ok</Text>
                </TouchableOpacity>
                <TouchableOpacity style={{backgroundColor:'red',width:'50%'}} onPress={()=>this.closeModal()}>
                <Text style={{color:'white',textAlign:'center',padding:10}}>Cancel</Text>
                </TouchableOpacity>
              </View>
            </View>
          </Modal>

        </View>


        <TouchableOpacity  onPress={()=>this.openFoodModal()}  underlayColor="white">
          <View style={styles.button}>
            <Text style={styles.buttonText}> Choose Food</Text>
          </View>
        </TouchableOpacity>

        <View>

          <Modal animationIn="slideInUp" 
              animationOut="slideOutDown" 
              onBackdropPress={()=>this.closeFoodModal()}
              onSwipeComplete={()=>this.closeFoodModal()} 
              swipeDirection="right" 
              isVisible={this.state.isFoodModalVisible} 
              width="100%"
              style={{backgroundColor:'white'}}>

          <View style={{ flex: 1,justifyContent:'center'}}>
          <Picker selectedValue = {this.state.food} 
              onValueChange = {this.updateFood} 
              style = {styles.input}>
                {this.state.foodData.map((foods, id) => {
                  return <Picker.Item 
                    value={foods.item} 
                    label={foods.description}
                    key={foods.id}  /> 

                    }
                    )}
            </Picker>

            <Text style = {styles.text}>Add Hours: </Text>

            <TextInput style = {styles.input}
              underlineColorAndroid = "transparent"
              placeholder = " Hours (fe 1.3)"
              placeholderTextColor = {Colors.primaryColor}
              autoCapitalize = "none"
              onChangeText = {this.handleHours}/>     
          </View>


          <View style={{ flex: 1,justifyContent:'center',position:'absolute',bottom:0}}>
              <View style={{flexDirection:'row',}}>
              <TouchableOpacity style={{backgroundColor:'red',width:'50%'}} onPress={()=>this.insertOrder()}>
                <Text style={{color:'white',textAlign:'center',padding:10}}>Ok</Text>
                </TouchableOpacity>
                <TouchableOpacity style={{backgroundColor:'red',width:'50%'}} onPress={()=>this.closeModal()}>
                <Text style={{color:'white',textAlign:'center',padding:10}}>Cancel</Text>
                </TouchableOpacity>
              </View>
            </View>
          </Modal>

        </View>



{/* 
