import React from 'react';
import {
  View,
  Text,
  StyleSheet
} from 'react-native';
import { AntDesign } from '@expo/vector-icons'; 
import { MaterialIcons } from '@expo/vector-icons'; 

import DefaultText from './DefaultText';
import Colors from '../constants/Colors';

const MealItem = props => {

  if (props.item === 'car')
  {
    const itemIcon = <AntDesign name="car" size={25} color= {Colors.primaryColor} /> 
  }
  else if (props.item === 'train')
  {
    const itemIcon = <MaterialIcons name="train" size={24} color="black" />
  }
  else{
    const itemIcon = <AntDesign name="car" size={25} color= {Colors.primaryColor} /> 
  }

  return (
    <View style={styles.mealItem}>
        <View style={{ ...styles.mealRow, ...styles.mealDetail }}>
          {{itemIcon}}
          <DefaultText> Type : {props.type}</DefaultText>
          <DefaultText> Item : {props.item}</DefaultText>
          <DefaultText> Time : {props.time}</DefaultText>
          <DefaultText> Cost : {props.cost}</DefaultText>
        </View>
    </View>
  );
};

const styles = StyleSheet.create({
  mealItem: {
    height: 200,
    width: '100%',
    backgroundColor: '#E7FFE1',
    borderRadius: 10,
    overflow: 'hidden',
    marginVertical: 10
  },
  bgImage: {
    width: '100%',
    height: '100%',
    justifyContent: 'flex-end',
  },
  mealRow: {
    flexDirection: 'row'
  },
  mealHeader: {
    height: '85%'
  },
  mealDetail: {
    paddingHorizontal: 10,
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '15%'
  },
  titleContainer: {
    backgroundColor: 'green',
    paddingVertical: 5,
    paddingHorizontal: 12
  },
  title: {
    fontFamily: 'open-sans-bold',
    fontSize: 20,
    color: 'white',
    textAlign: 'center'
  }
});

export default MealItem;
