import React from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  TouchableOpacity
} from 'react-native';
import { AntDesign } from '@expo/vector-icons'; 
import { MaterialIcons } from '@expo/vector-icons'; 
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import { FontAwesome } from '@expo/vector-icons'; 

import DefaultText from './DefaultText';
import Colors from '../constants/Colors';

const MealItem = props => {


  updateTransport = (transport) => {
    fetch('http://0.0.0.0:5000/getTransportObjects', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      method: 'GET'
    })
    .then((response) => response.json())
    .then((responseJson) => {
      console.log(responseJson);
      for (const key in responseJson['transportObjects']){
        // console.log("key:",responseJson['transportObjects'][key])
        this.state.transportData.push(responseJson['transportObjects'][key])
        }
        this.setState({isLoading: !this.state.isLoading })
    })
    .catch((error) => {
      console.error(error);
      alert("Transport data didnt fetch");

    });
  }

  updateTransport = (transport) => {
    this.setState({ transport: transport })
  }



  var itemIcon = <MaterialCommunityIcons name="train-car" size={24} color={Colors.primaryColor} /> 
    if (props.item === 'car'){
      itemIcon = <AntDesign name="car" size={25} color= {Colors.primaryColor} />
    }
    else if (props.item === 'train'){
      itemIcon = <MaterialIcons name="train" size={24} color= {Colors.primaryColor} />
    }
    else if (props.item === 'bus'){
      itemIcon = <MaterialIcons name="directions-bus" size={24} color={Colors.primaryColor} />    }

    else if (props.item === 'aeroplane'){
      itemIcon = <FontAwesome name="plane" size={24} color= {Colors.primaryColor} />
    }
  return (
    <View style={styles.mealItem}>
        <View style={{ ...styles.mealRow, ...styles.mealDetail }}>
          {itemIcon}
          <DefaultText> Type : {props.type} | Item : {props.item}</DefaultText>
          <DefaultText> Time : {props.time} | Cost : {props.cost}</DefaultText>
          <DefaultText> Date : {props.date}</DefaultText>
        </View>
        <TouchableOpacity>
          <MaterialIcons name="change-history" size={24} color={Colors.primaryColor} />
         </TouchableOpacity>
         <TouchableOpacity>
         <MaterialIcons name="delete" size={24} color={Colors.primaryColor} />
         </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  mealItem: {
    height: 200,
    width: '100%',
    backgroundColor: '#E7FFE1',
    borderRadius: 10,
    overflow: 'hidden',
    marginVertical: 10
  },
  bgImage: {
    width: '100%',
    height: '100%',
    justifyContent: 'flex-end',
  },
  mealRow: {
    flexDirection: 'row'
  },
  mealHeader: {
    height: '85%'
  },
  mealDetail: {
    paddingHorizontal: 10,
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '15%'
  },
  titleContainer: {
    backgroundColor: 'green',
    paddingVertical: 5,
    paddingHorizontal: 12
  },
  title: {
    fontFamily: 'open-sans-bold',
    fontSize: 20,
    color: 'white',
    textAlign: 'center'
  },
  buttons: {
    elevation: 8,
    backgroundColor: "green",
    borderRadius: 5,
    margin:5,
    paddingVertical: 10,
    paddingHorizontal: 12
  },
});

export default MealItem;
