import React, { Component } from 'react'
import { HeaderButtons, Item } from 'react-navigation-header-buttons';
import { View, Text, TextInput, StyleSheet, Button } from 'react-native';

import HeaderButton from '../components/HeaderButton';

import Colors from '../constants/Colors';

class SettingsScreen extends Component {

  state = {
    username : '',
    email: '',
    bugdet: 0,
    isLoading: true
  }

  componentDidMount = () => {
    fetch('http://0.0.0.0:5000/getCustomer', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
       method: 'GET'
    })
    .then((response) => response.json())
    .then((responseJson) => {
        console.log(responseJson);
        this.setState({username: responseJson['username'], bugdet: responseJson['bugdet'], email: responseJson['email'], isLoading: !this.state.isLoading })

    })
    .catch((error) => {
       console.error(error);
       alert("User data didnt fetch");
      });
    }


    updateSettings = (username, email, bugdet) => {
      alert('username: ' + username + ' email: ' + email +' bugdet: ' + bugdet)
    }

    render() {
      console.log(this.state);

      if(!this.state.loading) {
        return (
        <View>
          <TextInput style = {styles.input}
            underlineColorAndroid = "transparent"
            placeholder = {this.state.username}
            // placeholderTextColor = {Colors.primaryColor}
            autoCapitalize = "none"
            onChangeText = {this.handleHours}/>

          <Text style = {styles.text}>Update Email </Text>

          <TextInput style = {styles.input}
            underlineColorAndroid = "transparent"
            placeholder = {this.state.email}
            // placeholderTextColor = {Colors.primaryColor}
            autoCapitalize = "none"
            onChangeText = {this.handleHours}/>

          <Text style = {styles.text}>Update Bugdet </Text>

          <TextInput style = {styles.input}
            underlineColorAndroid = "transparent"
            placeholder = {this.state.bugdet}
            // placeholderTextColor = {Colors.primaryColor}
            autoCapitalize = "none"
            onChangeText = {this.handlebugdet}/>

          <Button
            style = {styles.submitButton}
            onPress = {() => this.updateSettings(this.state.username, this.state.email, this.state.bugdet)}
            title= "Save" 
            >
          </Button>
        </View>
        )
      }
      else{
        return (
          <ActivityIndicator size={"large"} ></ActivityIndicator>)
      }
    }

  // return <MealList listData={favMeals} navigation={props.navigation} />;
};

SettingsScreen.navigationOptions = navData => {
  return {
    headerTitle: 'Settings',
    headerLeft: (
      <HeaderButtons HeaderButtonComponent={HeaderButton}>
        <Item
          title="Menu"
          iconName="ios-menu"
          onPress={() => {
            navData.navigation.toggleDrawer();
          }}
        />
      </HeaderButtons>
    )
  };
};

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    alignItems: 'center'
  },
  title: {
    fontFamily: 'open-sans-bold',
    fontSize: 12,
    margin: 20,
    textAlign: 'center'
  },
  submitButton: {
    padding: 10,
    margin: 15,
    height: 40,
    color:"#841584"

  },
  submitButtonText:{
      color: 'white'
  },
  container: {
    paddingTop: 23
  },
  input: {
    margin: 15,
    height: 40,
    borderWidth: 1,
    padding: 15
  }
});

export default SettingsScreen;
