import React, { Component } from 'react'

import { View, Text, ScrollView, Button } from 'react-native';
import { SafeAreaView } from 'react-navigation'


class CustomDrawerContentComponent extends Component {
    render() {
      const { theme, user } = this.props;

      return (
        <View style={{ flex: 1 }}>

          <ScrollView>
            <SafeAreaView
              style={styles.container}
              forceInset={{ top: 'always', horizontal: 'never' }}
            >
              <View style={[ styles.containHeader, { backgroundColor: theme.pri700 }]}>
                <View style={{ justifyContent: 'center', alignItems: 'center' }}>
                  <Avatar size='large' rounded icon={{ name: 'user-circle-o', type: 'font-awesome', size: 80 }} />
                  <Text style={{ color: '#f9f9f9', marginTop: '3%', fontFamily: 'sans-serif-condensed' }}>{`Hi ${user.firstname}`}</Text>
                  <Text style={{ color: '#f9f9f9', fontFamily: 'sans-serif-condensed' }}>{`${user.email}`}</Text>
                </View>
              </View>

              <DrawerItems {...this.props} />

              <View>
                <View style={{ marginTop: '2%' }}>
                  <Divider style={{ backgroundColor: '#777f7c90' }} />
                </View>
                <View style={{ marginTop: '3%' }}>
                  <ColorPalette />
                </View>
                <View style={{ marginTop: '5%' }}>
                  <Divider style={{ backgroundColor: '#777f7c90' }} />
                </View>
              </View>
            </SafeAreaView>
          </ScrollView>

        </View>
      );
    }
  }
  const styles = StyleSheet.create({
    gridItem: {
      flex: 1,
      margin: 15,
      height: 150,
      borderRadius: 10,
      overflow:
        Platform.OS === 'android' && Platform.Version >= 21
          ? 'hidden'
          : 'visible',
      elevation: 5
    },
    container: {
      flex: 1,
      borderRadius: 10,
      shadowColor: 'black',
      shadowOpacity: 0.26,
      shadowOffset: { width: 0, height: 2 },
      shadowRadius: 10,
      padding: 15,
      justifyContent: 'flex-end',
      alignItems: 'flex-end'
    },
    title: {
      fontFamily: 'open-sans-bold',
      fontSize: 22,
      textAlign: 'right'
    }
  });
  export default CustomDrawerContentComponent;
