import React, { Component } from 'react'
import {
  View,
  Text,
  TextInput,
  ActivityIndicator,
  StyleSheet,
  Picker,
  Button,
} from 'react-native';
import { HeaderButtons, Item } from 'react-navigation-header-buttons';
// import ProgressCircle from 'react-native-progress-circle'

import HeaderButton from '../components/HeaderButton';
import Colors from '../constants/Colors';
import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  ContributionGraph
} from 'react-native-chart-kit'

class CategoriesScreen extends Component {

  state = {
    email: '',
    password: '',
    user: '',
    energyType:'',
    transport: '',
    food: '',
    hours: '',
    transportData: [],
    foodData: [],
    isLoading: true,
    isDisabled: true
 }


  componentDidMount = () => {
    fetch('http://0.0.0.0:5000/getTransportObjects', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      method: 'GET'
    })
    .then((response) => response.json())
    .then((responseJson) => {
      console.log(responseJson);
      for (const key in responseJson['transportObjects']){
        // console.log("key:",responseJson['transportObjects'][key])
        this.state.transportData.push(responseJson['transportObjects'][key])
        }
      for (const key in responseJson['foodObjects']){
        // console.log("key:",responseJson['transportObjects'][key])
        this.state.foodData.push(responseJson['foodObjects'][key])
        }
        this.setState({isLoading: !this.state.isLoading })
    })
    .catch((error) => {
      console.error(error);
      alert("Transport data didnt fetch");

    });
  }



  insertOrder = (energyType, transport, hours) => {
    fetch('http://0.0.0.0:5000/insertOrder', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      method: 'POST',
      body: JSON.stringify({
        // energyType: energyType,
        item: transport,
        time: hours
     })
    })
    .then((response) => response.json())
    .then((responseJson) => {
      console.log(responseJson);
    })
    .catch((error) => {
      console.error(error);
      alert("Insert Order Error");

    });
  }

  updateTransport = (transport) => {
    this.setState({ transport: transport })
  }

  updateFood = (food) => {
    this.setState({ food: food })
  }

  handleEmail = (text) => {
    this.setState({ email: text })
  }
  handlePassword = (text) => {
    this.setState({ password: text })
  }
  handleHours = (float) => {
    this.setState({ hours: float })
  }
  handleTransport = (text) => {
  this.setState({ transport: text })
  }
  login = (transport, hours) => {
    alert('Transport: ' + transport + ' Hours: ' + hours)
  }
  render() {

    console.log(this.state.transportData);
    console.log(this.state.foodData);

    const data = [
      { name: 'Seoul', population: 21500000, color: 'rgba(131, 167, 234, 1)', legendFontColor: '#7F7F7F', legendFontSize: 15 },
      { name: 'Toronto', population: 2800000, color: '#F00', legendFontColor: '#7F7F7F', legendFontSize: 15 },
      { name: 'Beijing', population: 527612, color: 'red', legendFontColor: '#7F7F7F', legendFontSize: 15 },
      { name: 'New York', population: 8538000, color: '#ffffff', legendFontColor: '#7F7F7F', legendFontSize: 15 },
      { name: 'Moscow', population: 11920000, color: 'rgb(0, 0, 255)', legendFontColor: '#7F7F7F', legendFontSize: 15 }
    ]

    const chartConfig = {
      backgroundGradientFrom: '#1E2923',
      backgroundGradientTo: '#08130D',
      color: (opacity = 1) => `rgba(26, 255, 146, ${opacity})`
    }


    if(!this.state.loading) {
      return (
        <View style = {styles.container}>
            {/* <PieChart
              style={styles.pieChart}
              width = {600}
              data={data}
              height={220}
              chartConfig={chartConfig}
              accessor="population"
              backgroundColor="transparent"
              paddingLeft={15}
            /> */}
            {/* <Progress.Circle size={30} indeterminate={true} /> */}

            {/* <ProgressCircle
                percent={30}
                radius={50}
                borderWidth={8}
                color="#3399FF"
                shadowColor="#999"
                bgColor="#fff"
            >
                <Text style={{ fontSize: 18 }}>{'30%'}</Text>
            </ProgressCircle> */}

            <Text style = {styles.text}>Choose Transportation: </Text>

            <Picker selectedValue = {this.state.transport} 
              onValueChange = {this.updateTransport} 
              style = {styles.input}>
                {this.state.transportData.map((transports, id) => {
                  return <Picker.Item 
                    value={transports.item} 
                    label={transports.description}
                    key={transports.id}  /> 

                    }
                    )}
            </Picker>

            <Text style = {styles.text}>Choose Food: </Text>

            <Picker selectedValue = {this.state.food} 
              onValueChange = {this.updateFood} 
              style = {styles.input}>
                {this.state.foodData.map((foods, id) => {
                  return <Picker.Item 
                    value={foods.item} 
                    label={foods.description}
                    key={foods.id}  /> 

                    }
                    )}
            </Picker>

            <Text style = {styles.text}>{this.state.transport}</Text>

            <Text style = {styles.text}>Add Hours: </Text>

            <TextInput style = {styles.input}
              underlineColorAndroid = "transparent"
              placeholder = " Hours (fe 1.3)"
              placeholderTextColor = {Colors.primaryColor}
              autoCapitalize = "none"
              onChangeText = {this.handleHours}/>

            <Button
              style = {styles.submitButton}
              onPress = {this.insertOrder}
              title= "Submit" 
              // disabled={this.state.isDisabled}
              >
            </Button>
        </View>

      )
    }
    else{
      return(<ActivityIndicator size={"large"} ></ActivityIndicator>)
    }
  }
}

CategoriesScreen.navigationOptions = navData => {
  return {
    headerTitle: 'My Energy',
    headerLeft: (
      <HeaderButtons HeaderButtonComponent={HeaderButton}>
        <Item
          title="Menu"
          iconName="ios-menu"
          onPress={() => {
            navData.navigation.toggleDrawer();
          }}
        />
      </HeaderButtons>
    )
  };
};

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'left'
  },
  container: {
      paddingTop: 5,
      flex:1
  },
  input: {
      margin: 5,
      height: 30,
      borderColor: Colors.primaryColor,
      borderWidth: 1
  },
  pieChart:{
    padding: 10,
    marging: 10
  },
  submitButton: {
      backgroundColor: Colors.primaryColor,
      padding: 10,
      margin: 15,
      height: 40,
  },
  submitButtonText:{
      color: 'white'
  },
  text: {
    fontSize: 20,
    alignSelf: "flex-start",
    color: Colors.primaryColor,
    padding: 15,
    margin: 2
 }


});

export default CategoriesScreen;
